import {
  HeartOutlined,
  SearchOutlined,
  ShoppingCartOutlined,
} from "@ant-design/icons";
import {
  Col,
  Divider,
  Form,
  Image,
  InputNumber,
  Modal,
  Row,
  Select,
} from "antd";
import { useState, React, useEffect } from "react";
import { addToCart } from "../../../../store/productSlice";
import { useDispatch } from "react-redux";
import "./styles.scss";
import instance from "../../../../utils/AxiosConfig/AxiosConfig";
import { Link } from "react-router-dom";
const { Option } = Select;

const SingleProduct = ({ product }) => {
  const [form] = Form.useForm();
  const dispatch = useDispatch();

  const [visibleModalAddToCart, setVisibleModalAddToCart] = useState(false);
  const [singleProduct, setSingleProduct] = useState({});

  const handleAddToCart = (values) => {
    const submitData = {
      ...values,
      name: product.name,
      image: product.image,
      description: product.description,
    };
    dispatch(addToCart(submitData));
    setVisibleModalAddToCart(false);
  };

  const onFinish = () => {
    form
      .validateFields()
      .then((values) => {
        form.resetFields();
        handleAddToCart(values);
      })
      .catch((info) => {
        console.log("Validate Failed:", info);
      });
  };

  const handleCancel = () => {
    setVisibleModalAddToCart(false);
  };

  const getProduct = async (id) => {
    const { data } = await instance.get(`products/${id}`);
    data && setSingleProduct(data);
  };
  const handleClick = (id) => {
    setVisibleModalAddToCart(true);
    getProduct(id);
  };

  const colorOption = product.colors.map((color) => color.color);
  const selectedColor = colorOption[0];
  const [sizeOption, setSizeOption] = useState([]);
  const [selectedSize, setSelectSize] = useState(sizeOption[0]);
  const [bigImage, setBigImage] = useState(product.image[0].src);

  useEffect(() => {
    product.colors.forEach((item) => {
      if (item.color === selectedColor) {
        const size = item.sizes.map((size) => size.size);
        setSizeOption(size);
      }
    });
  }, [selectedColor]);

  useEffect(() => {
    form.setFieldsValue({
      size: sizeOption[0],
    });
  }, [sizeOption, visibleModalAddToCart]);

  // update size after changing color
  const onChangeColor = (selectedColor) => {
    product.colors.forEach((item) => {
      if (item.color === selectedColor) {
        const size = item.sizes.map((size) => size.size);
        setSizeOption(size);
      }
    });
  };

  return (
    <>
      {singleProduct && (
        <Modal
          centered
          visible={visibleModalAddToCart}
          footer={null}
          onCancel={handleCancel}
          className="product__details__wrapper"
          width={700}
        >
          <Row gutter={[16, 0]}>
            <Col span={12}>
              <Image
                src={bigImage}
                fallback="data:image/png;base64,"
              />
              <div className="image__group">
                {singleProduct.image
                  ? singleProduct.image.map((item, index) => {
                      return (
                        <Image
                          preview={false}
                          src={item.src}
                          key={index}
                          onClick={() => setBigImage(item.src)}
                        />
                      );
                    })
                  : ""}
              </div>
            </Col>
            <Col className="product__details__content" span={12}>
              <h1>{product.name}</h1>
              <Divider />
              <span>{product.description}</span>
              <Divider />
              <Form
                layout="vertical"
                initialValues={{
                  size: selectedSize,
                  color: selectedColor,
                  quantity: 1,
                }}
                form={form}
                onFinish={onFinish}
              >
                <Form.Item name="color" label="Color">
                  <Select
                    style={{ width: "100%" }}
                    onSelect={(item) => onChangeColor(item)}
                  >
                    {colorOption.map((color, index) => {
                      return (
                        <Option value={color} key={index}>
                          {color}
                        </Option>
                      );
                    })}
                  </Select>
                </Form.Item>

                <Form.Item name="size" label="Size">
                  <Select style={{ width: "100%" }}>
                    {sizeOption
                      ? sizeOption
                          .sort((a, b) => a - b)
                          .map((size, index) => {
                            return (
                              <Option value={size} key={index}>
                                {size}
                              </Option>
                            );
                          })
                      : ""}
                  </Select>
                </Form.Item>

                <div
                  style={{
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "space-between",
                  }}
                >
                  <Form.Item name="quantity" label="Quantity">
                    <InputNumber min={1} />
                  </Form.Item>

                  <button className="add-to-cart__btn" type="submit">
                    <ShoppingCartOutlined
                      style={{ fontSize: "20px", marginRight: "5px" }}
                    />
                    Add to cart
                  </button>
                </div>
              </Form>
            </Col>
          </Row>
        </Modal>
      )}
      <div className="single__product__wrapper">
        <div className="single__product__image">
          <div className="single__product__image__contain">
            <Image
              src={product.image[0].src}
              fallback="data:image/png;base64,"
            />
            <div className="single__product__image__boxmodal">
              <div className="single__product__icon">
                <span>
                  <HeartOutlined className="single__product__icon__item" />
                </span>
                <span onClick={() => handleClick(product.id)}>
                  <SearchOutlined className="single__product__icon__item" />
                </span>
              </div>
            </div>
          </div>
        </div>
        <div className="single__product__content">
          <Link
            className="single__product__content__link"
            to={`/details/${product.id}`}
          >
            <h4>{product.name}</h4>
          </Link>
          <p>${product.price}</p>
        </div>
      </div>
    </>
  );
};

export default SingleProduct;
