import React, { useState } from "react";
import "./styles.scss";
import { HeartOutlined, SearchOutlined } from "@ant-design/icons";
import { Image, Row, Col, Divider, Select, Space } from "antd";
const { Option } = Select;
import { Modal } from "antd";
import instance from "../../../../utils/AxiosConfig/AxiosConfig";
import { useDispatch, useSelector } from "react-redux";
import { addToCart } from "../../../../store/productStore";

const SingleProduct = ({ product }) => {
  const dispatch = useDispatch();
  const [singleProduct, setSingleProduct] = useState({});
  const getProduct = async (id) => {
    const { data } = await instance.get(`products/${id}`);
    data && setSingleProduct(data);
    return data;
  };

  const cartItem = useSelector((state) => state.product.cartItem);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const handleAddToCart = async (id) => {
    const data = await getProduct(id);
    data && dispatch(addToCart(data));
  };
  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  const handleClick = (id) => {
    setIsModalVisible(true);
    getProduct(id);
  };

  return (
    <>
      {singleProduct && (
        <Modal
          centered
          title="Product Details"
          visible={isModalVisible}
          onOk={() => {
            handleOk();
            handleAddToCart(product.id);
          }}
          onCancel={handleCancel}
          className="product__details__wrapper"
        >
          <Row gutter={[16, 0]}>
            <Col span={12}>
              <Image
                src={singleProduct.image}
                fallback="data:image/png;base64,"
              />
            </Col>
            <Col className="product__details__content" span={12}>
              <h1>{singleProduct.name}</h1>
              <Divider />
              <span>{singleProduct.description}</span>
              <Divider />
              <div>
                <span style={{ marginRight: "10px" }}>Size:</span>
                <Select style={{ width: "100%" }} defaultValue="38">
                  <Option>38</Option>
                  <Option>39</Option>
                  <Option>40</Option>
                </Select>
              </div>
              <div style={{ margin: "10px 0" }}>
                <span style={{ marginRight: "10px" }}>Color:</span>
                <Select style={{ width: "100%" }} defaultValue="Navy">
                  <Option>Navy</Option>
                  <Option>Another Navy</Option>
                  <Option>Navy again</Option>
                </Select>
              </div>
            </Col>
          </Row>
        </Modal>
      )}
      <div className="single__product__wrapper">
        <div className="single__product__image">
          <div className="single__product__image__contain">
            <Image
              src={product.image}
              fallback="data:image/png;base64,"
            />
            <div className="single__product__image__boxmodal">
              <div className="single__product__icon">
                <span>
                  <HeartOutlined />
                </span>
                <span onClick={() => handleClick(product.id)}>
                  <SearchOutlined />
                </span>
              </div>
            </div>
          </div>
        </div>
        <div className="single__product__content">
          <h3>{product.name}</h3>
          <h5>${product.price}</h5>
        </div>
        <div className="single__product__action">
          <button
            onClick={() => handleAddToCart(product.id)}
            style={{ padding: "7px 0" }}
          >
            Add to cart
          </button>
        </div>
      </div>
    </>
  );
};

export default SingleProduct;
