import {
  HeartOutlined,
  SearchOutlined,
  ShoppingCartOutlined,
} from "@ant-design/icons";
import {
  Button,
  Col,
  Divider,
  Form,
  Image,
  InputNumber,
  Modal,
  Row,
  Select,
} from "antd";
import { useState, React, useEffect } from "react";
import { addToCart } from "../../../../store/productSlice";
import { useDispatch } from "react-redux";
import instance from "../../../../utils/AxiosConfig/AxiosConfig";
import "./styles.scss";
import { size } from "lodash";
const { Option } = Select;

const SingleProduct = ({ product }) => {
  const [form] = Form.useForm();
  const dispatch = useDispatch();
  const [singleProduct, setSingleProduct] = useState({});

  const getProduct = async (id) => {
    const { data } = await instance.get(`products/${id}`);
    data && setSingleProduct(data);
    // console.log(singleProduct);
    // return data;
  };

  const [isModalVisible, setIsModalVisible] = useState(false);

  const handleAddToCart = (values) => {
    // console.log(values);
    // console.log(product);
    const submitData = {
      ...values,
      name: product.name,
      image: product.image,
      description: product.description,
    };
    dispatch(addToCart(submitData));
    setIsModalVisible(false);
  };

  const onFinish = () => {
    form
      .validateFields()
      .then((values) => {
        form.resetFields();
        handleAddToCart(values);
      })
      .catch((info) => {
        console.log("Validate Failed:", info);
      });
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  const handleClick = (id) => {
    setIsModalVisible(true);
    getProduct(id);
  };

  const colorOption = product.colors.map((color) => color.color);
  const selectedColor = colorOption[0];
  const [sizeOption, setSizeOption] = useState([]);
  const [selectedSize, setSelectedSize] = useState(sizeOption[0]);

  useEffect(() => {
    product.colors.forEach((item) => {
      if (item.color === selectedColor) {
        const size = item.sizes.map((size) => size.size);
        setSizeOption(size);
      }
    });
  }, [selectedColor]);

  useEffect(() => {
    form.setFieldsValue({
      size: sizeOption[0],
    });
  }, [sizeOption]);

  // handle color change
  const onChangeColor = (selectedColor) => {
    product.colors.forEach((item) => {
      if (item.color === selectedColor) {
        const size = item.sizes.map((size) => size.size);
        setSizeOption(size);
      }
    });
  };

  return (
    <>
      {singleProduct && (
        <Modal
          centered
          visible={isModalVisible}
          footer={null}
          onCancel={handleCancel}
          className="product__details__wrapper"
        >
          <Row gutter={[16, 0]}>
            <Col span={12}>
              <Image
                src={singleProduct.image}
                fallback="data:image/png;base64,"
              />
            </Col>
            <Col className="product__details__content" span={12}>
              <h1>{singleProduct.name}</h1>
              <Divider />
              <span>{singleProduct.description}</span>
              <Divider />
              <Form
                layout="vertical"
                initialValues={{
                  size: selectedSize,
                  color: selectedColor,
                  quantity: 1,
                }}
                form={form}
                onFinish={onFinish}
              >
                <Form.Item name="color" label="Color">
                  <Select
                    style={{ width: "100%" }}
                    onSelect={(item) => onChangeColor(item)}
                  >
                    {colorOption.map((color, index) => {
                      return (
                        <Option value={color} key={index}>
                          {color}
                        </Option>
                      );
                    })}
                  </Select>
                </Form.Item>

                <Form.Item name="size" label="Size">
                  <Select style={{ width: "100%" }}>
                    {sizeOption
                      ? sizeOption
                          .sort((a, b) => a - b)
                          .map((size, index) => {
                            return (
                              <Option value={size} key={index}>
                                {size}
                              </Option>
                            );
                          })
                      : ""}
                  </Select>
                </Form.Item>

                <Form.Item name="quantity" label="Quantity">
                  <InputNumber min={1} />
                </Form.Item>

                <Button type="primary" htmlType="submit">
                  <ShoppingCartOutlined
                    style={{ fontSize: "20px", marginRight: "5px" }}
                  />
                  Add to cart
                </Button>
              </Form>
            </Col>
          </Row>
        </Modal>
      )}
      <div className="single__product__wrapper">
        <div className="single__product__image">
          <div className="single__product__image__contain">
            <Image
              src={product.image}
              fallback="data:image/png;base64,"
            />
            <div className="single__product__image__boxmodal">
              <div className="single__product__icon">
                <span>
                  <HeartOutlined />
                </span>
                <span onClick={() => handleClick(product.id)}>
                  <SearchOutlined />
                </span>
              </div>
            </div>
          </div>
        </div>
        <div className="single__product__content">
          <h3>{product.name}</h3>
          <h4>${product.price}</h4>
        </div>
        <div className="single__product__action">
          <button onClick={() => setIsModalVisible(true)}>
            <ShoppingCartOutlined
              style={{ fontSize: "20px", marginRight: "5px" }}
            />
            Add to cart
          </button>
        </div>
      </div>
    </>
  );
};

export default SingleProduct;
